import React, { useState } from "react";
import { GrLogout } from "react-icons/gr";
import { IconContext } from "react-icons";
import { FaUsers, FaChartBar } from "react-icons/fa";
import { Link, useNavigate } from "react-router-dom";
import Cookies from "js-cookie";
import "./SampleSideBar.css";
function SideBar() {
  const navigate = useNavigate();
  const role = Cookies.get("role");
  const logout = () => {
    console.log("logout working");
    Cookies.remove("role");
    Cookies.remove("id");
    Cookies.remove("jwtToken");
    navigate("/login");
  };

  const [isOpen, setIsOpen] = useState(false);

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  console.log(role, "check");
  return (
    <>
      <div className={`sidebar ${isOpen ? "--is-open" : ""}`}>
        <div className="logo">
          <h1 className="heading-admin">
            {role === "user" ? "User" : "Admin"} Dashboard
          </h1>
        </div>
        <div className="icons">
          <div className="icon">
            {role === "admin" ? (
              <>
                <Link to="/adminhome" className="link">
                  <IconContext.Provider
                    value={{ color: "white", className: "global-class-name" }}
                  >
                    <FaChartBar />
                  </IconContext.Provider>
                </Link>
              </>
            ) : (
              <>
                <Link to="/userhome" className="link">
                  <IconContext.Provider
                    value={{ color: "white", className: "global-class-name" }}
                  >
                    <FaChartBar />
                  </IconContext.Provider>
                </Link>
              </>
            )}
          </div>
          <div className="icon">
            {role === "admin" ? (
              <>
                <Link to="/executies">
                  <IconContext.Provider
                    value={{ color: "white", className: "global-class-name" }}
                  >
                    <FaUsers />
                  </IconContext.Provider>
                </Link>
              </>
            ) : (
              <>
                <Link to="/users">
                  <IconContext.Provider
                    value={{ color: "white", className: "global-class-name" }}
                  >
                    <FaUsers />
                  </IconContext.Provider>
                </Link>
              </>
            )}
          </div>

          <div className="icon" onClick={logout}>
            <IconContext.Provider
              value={{ color: "white", className: "global-class-name" }}
            >
              <GrLogout />
            </IconContext.Provider>
          </div>
        </div>
      </div>
      {/* <div className="sidebar-container">
        <div className="admin-container">
          <div className="header-name">
            <IconContext.Provider
              value={{ color: "white", className: "global-class-name" }}
            >
              <div>
                <GiHamburgerMenu />
              </div>
            </IconContext.Provider>
            <h1 className="heading-admin">
              {role === "user" ? "User" : "Admin"}
            </h1>
          </div>
        </div>
        <div className="list-container">
          {role === "admin" ? (
            <>
              <Link to="/adminhome" className="link">
                <p className="sidebar-para-list">Dashboard</p>
              </Link>
              <Link to="/executies" className="link">
                <p className="sidebar-para-list"> Execuites</p>
              </Link>
            </>
          ) : (
            <>
              <Link to="/userhome" className="link">
                <p className="sidebar-para-list">Dashboard</p>
              </Link>
              <Link to="/users" className="link">
                <p className="sidebar-para-list">Users</p>
              </Link>
            </>
          )}
        </div>

        <div className="logout-container">
          <button className="sidebar-button" onClick={logout}>
            logout
          </button>
        </div>
      </div> */}
