import { Paper } from "@mui/material";
import { Box } from "@mui/system";
import React, { useEffect } from "react";
import {
  BarChart,
  Bar,
  Cell,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer,
} from "recharts";

import "./index.css";

const da = [
  {
    status_count: {
      active_users: 2,
      inactive_users: 1,
    },
    user_data: [
      {
        username: "ravisabbi",
        no_surveys: 3,
      },
      {
        username: "jb",
        no_surveys: 2,
      },
      {
        username: "naiduKotha",
        no_surveys: 3,
      },
    ],
  },
];
console.log(da[0].user_data);
const data = da[0].user_data;

const BarCharts = () => {
  const [chartData, setchartData] = useState([])
  useEffect(()=>{})
  return (
    <div className="barchart-bg-container">
      {/* active-container */}
      <div className="count-container">
        <div className="count-container-1">
          <h1 className="heading-count">Active users</h1>
          <p className="para-count">{da[0].status_count.active_users}</p>
        </div>
        <div className="count-container-2">
          <h1 className="heading-count">Inactive users</h1>
          <p className="para-count">{da[0].status_count.inactive_users}</p>
        </div>
      </div>

      {/* <ResponsiveContainer> */}
      <div className="barchart-container">
        <BarChart
          width={500}
          height={300}
          data={data}
          margin={{
            top: 5,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="username" />
          <YAxis />
          <Tooltip />

          <Bar dataKey="no_surveys" fill="#8884d8" barSize={40} />
        </BarChart>
      </div>
      {/* </ResponsiveContainer> */}
    </div>
  );
};

export default BarCharts;
