export default function CustomPaginationActionsTable() {
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(5);

  // Avoid a layout jump when reaching the last page with empty rows.
  const emptyRows =
    page > 0 ? Math.max(0, (1 + page) * rowsPerPage - rows.length) : 0;

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };
  const handleAddUserOpen = () => {
    setAddUserOpen(true);
  };
  const handleAddUserClose = () => {
    setAddUserOpen(false);
    setEmail("");
    setEmailError("");
  };
  const [rowValues, setRowValues] = React.useState([]);
  const [addUserOpen, setAddUserOpen] = React.useState(false);
  const [email, setEmail] = React.useState("");
  const [emailError, setEmailError] = React.useState("");
  console.log(rowValues);
  const storeEmail = (event) => {
    setEmail(event.target.value);
    const emailRegex = /\S+@\S+\.\S+/;
    if (event.target.value == "") {
      setEmailError("Enter Email");
    } else if (!emailRegex.test(event.target.value)) {
      setEmailError("Invalid Email");
    } else {
      setEmailError("");
    }
  };

  const sendMailData = () => {
    const userId = Cookies.get("id");
    if (email == "") {
      setEmailError("Enter Email");
    } else if (emailError === "") {
      //  logic here
      axios
        .post(url.API + "survey", { email, user_id: userId })
        .then((response) => {
          setEmail("");
          // toast sucesss
          toast.success(" Mail sent", {
            position: "top-right",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "light",
          });
          setTimeout(() => {
            setAddUserOpen(false);
          }, 2000);
          console.log(response);
        })
        .catch((error) => {
          setEmail("");
          toast.error(" Mail  not sent", {
            position: "top-right",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "light",
          });
          console.log(error.data);
        });
      handleAddUserClose();
    }
  };
  const getSurveyData = () => {
    axios
      // add id here
      .get(url.API + `userSurveys/${3}`)
      .then((response) => {
        if (response.statusText === "OK") {
          console.log(response);
          setRowValues(response.data);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  };
  React.useEffect(() => {
    getSurveyData();
  }, []);
  return (
    <div className="table-testing-container">
      <div>
        <Box
          sx={{
            display: "flex",
            flexDirection: "row",
            alignItems: "flex-end",
            justifyContent: "space-between",
          }}
        >
          <button className="table-search-button" onClick={handleAddUserOpen}>
            Invite
          </button>
          <Dialog open={addUserOpen} onClose={handleAddUserClose}>
            <DialogTitle>Invite</DialogTitle>
            <DialogContent>
              <DialogContentText>
                please enter email address here.
              </DialogContentText>

              <TextField
                autoFocus
                margin="dense"
                id="name"
                label="Email Address"
                type="email"
                fullWidth
                variant="standard"
                value={email}
                onChange={(e) => storeEmail(e)}
                error={emailError}
                helperText={emailError}
              />
            </DialogContent>
            <DialogActions>
              <Button onClick={handleAddUserClose}>Cancel</Button>
              <Button onClick={sendMailData} type="button">
                Send
              </Button>
            </DialogActions>
          </Dialog>
        </Box>
      </div>
      <Paper sx={{ maxWidth: "1200px" }}>
        <TableContainer>
          <Table sx={{ minWidth: 500 }} aria-label="custom pagination table">
            <TableHead>
              <TableRow>
                <StyledTableCell>
                  {" "}
                  <ArrowUpwardIcon />
                  Id <ArrowDownwardIcon />
                </StyledTableCell>

                <StyledTableCell>Email</StyledTableCell>
                <StyledTableCell>Date</StyledTableCell>
                <StyledTableCell>Status</StyledTableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {rowValues
                .sort((a, b) => (a.id > b.id ? -1 : 1))
                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                .map((row) => (
                  <StyledTableRow key={row.id}>
                    <StyledTableCell style={{ width: 100 }}>
                      {row.id}
                    </StyledTableCell>
                    <StyledTableCell
                      style={{ width: 100 }}
                      component="th"
                      scope="row"
                    >
                      {row.email}
                    </StyledTableCell>

                    <StyledTableCell style={{ width: 100 }}>
                      {row.survey_date}
                    </StyledTableCell>

                    <StyledTableCell style={{ width: 100 }}>
                      {row.status ? "Completed" : "Incompleted"}
                    </StyledTableCell>
                  </StyledTableRow>
                ))}

              {emptyRows > 0 && (
                <TableRow style={{ height: 53 * emptyRows }}>
                  <TableCell colSpan={6} />
                </TableRow>
              )}
            </TableBody>
            <TableFooter>
              <TableRow>
                <TablePagination
                  rowsPerPageOptions={[5, 10, 25, { label: "All", value: -1 }]}
                  colSpan={3}
                  count={rows.length}
                  rowsPerPage={rowsPerPage}
                  page={page}
                  SelectProps={{
                    inputProps: {
                      "aria-label": "rows per page",
                    },
                    native: true,
                  }}
                  onPageChange={handleChangePage}
                  onRowsPerPageChange={handleChangeRowsPerPage}
                  ActionsComponent={TablePaginationActions}
                />
              </TableRow>
            </TableFooter>
          </Table>
        </TableContainer>
      </Paper>
    </div>
  );
}
